$(document).ready(function(){init_date_ymd();markActiveMenu()});function markActiveMenu(){var a=window.location;$("ul.nav.nav-sidebar a").filter(function(){return this.href==a}).parent().addClass("active");$("ul.nav-treeview a").filter(function(){return this.href==a}).parentsUntil(".nav-sidebar > .nav-treeview").addClass("active")}function init_date_ymd(){$(".date-ymd").datepicker({format:"dd M yyyy",todayBtn:"linked",clearBtn:true,autoclose:true,todayHighlight:true})}function simple_alert(c,b,a){$.alert({title:c,content:b,type:a,})}function simple_alert_linkto(d,c,a,b){$.confirm({title:d,content:c,type:a,buttons:{Confirm:function(){location.href=b}}})};